import { Avatar } from '@material-ui/core';
import VideocamIcon from '@material-ui/icons/Videocam';
import PhotoLibraryIcon from '@material-ui/icons/PhotoLibrary';
import React, { useState } from 'react';
import styled, { css } from 'styled-components';
import { useStateValue } from './StatePrivider';
import db from '../firebase';
import firebase from 'firebase';
const storage = firebase.storage();



const DivContainer = styled.div`
  display:flex;
  flex-direction:column;
  margin-top:30px;
  background-color:white;
  border-radius:15px;
  box-shadow:0px 5px 7px -7px rgba(0,0,0,0.75);
  width:100%;
`
const TopContainer = styled.div`
  display:flex;
  border-bottom:1px solid #eff2f5;
  padding:15px;
`
const BottomContainer = styled.div`
  display:flex;
  justify-content:space-evenly;
`
// const Form = styled.form`

//   flex:1;

// `
const IputStyle = css`
  outline-width:0px;
  border:none;
  padding:5px 20px;
  margin:0px 10px;
  border-radius:999px;
  background-color:#eff2f5;
`
const Description = styled.input`
  ${IputStyle}
  flex:1;
`
const Prix = styled.input`
  ${IputStyle}
`
const Protable = styled.input`
  ${IputStyle}
`
const BottomOption = styled.div`
  padding:20px;
  display:flex;
  align-items:center;
  color:gray;
  margin:5px;
  h3{
    font-size:medium;
    margin-left:10px;
  }
  &:hover{
    background-color:#eff2f5;
    border-radius:20px;
    cursor:pointer;
  }
`
export const MessageSender = () => {
  const [desc, setDesc] = useState('')
  const [prix, setPrix] = useState('')
  const [phon,setPhon] = useState('')
  const [selecImgs, setSelectImgs] = useState(null)
  const [url, setUrl] = useState("");
  const [progress, setProgress] = useState(0);

  const onChangeImg = (e) => {
    // console.log(e.target.files)
    if (e.target.files[0]) {
      setSelectImgs(e.target.files[0])
    }
  }
const  handleSubmit = (e) => {
    e.preventDefault();
    if (selecImgs) {

    const uploadTask = storage.ref(`images/${selecImgs.name}`).put(selecImgs); 
    uploadTask.on(
      "state_changed",
      snapshot => {
        const progress = Math.round(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );
        setProgress(progress);
      },
      error => {
        console.log(error);
      },
      () => {
        storage
          .ref("images")
          .child(selecImgs.name)
          .getDownloadURL()
          .then(url => {
            setUrl(url);
          });
      }
      );
    }
     db.collection('posts').add(
      {
        message: desc,
        timestamp: firebase.firestore.FieldValue.serverTimestamp(),
        profilePic: user.photoURL,
        phon: phon,
        price: prix,
        image:url,
        }
      )

    // reset value
    setDesc('')
    setPrix('')
    setPhon('')
    setUrl('')
    setSelectImgs('')


};


const [{ user }, dispatch] = useStateValue();
return (

    <DivContainer>
    <form >
      <TopContainer>

        <Avatar src={user.photoURL}/>

        <Description value={desc}
          autofocus="autofocus"
            onChange={(e) => setDesc(e.target.value)} />
        <Prix type='number' placeholder='Prix' value={prix}
            onChange={(e) => setPrix(e.target.value)} />
          <Protable type='number' placeholder='Numero de telephone'
            value={phon}
          onChange={(e) => setPhon(e.target.value)} 
        />
        <button onClick={handleSubmit} type='submit' style={{ display: 'none' }}>hiden button</button>


      </TopContainer>
      </form>
      <BottomContainer>
        <BottomOption>
          <VideocamIcon style={{ color: 'red' }} />
          <h3>Live Video</h3>
        </BottomOption>
        <BottomOption>

        <label htmlFor="upload-photo" style={{display:'flex',alignItems:'center',cursor:'pointer'}}>

            <input
              type="file"  style={{ display: 'none', cursor: 'pointer' }}
                id='upload-photo'
            name="upload-photo"
            hidden='true'

              onChange={onChangeImg}
              />              
              <PhotoLibraryIcon style={{ color: 'green' }}/>
            <h3>Photos</h3>
            </label>

        </BottomOption>
        </BottomContainer>

    </DivContainer>
  )
}
