import { Avatar } from '@material-ui/core';
import VideocamIcon from '@material-ui/icons/Videocam';
import PhotoLibraryIcon from '@material-ui/icons/PhotoLibrary';
import React from 'react';
import styled, { css } from 'styled-components';

const DivContainer = styled.div`
  display:flex;
  flex-direction:column;
  margin-top:30px;
  background-color:white;
  border-radius:15px;
  box-shadow:0px 5px 7px -7px rgba(0,0,0,0.75);
  width:100%;
`
const TopContainer = styled.div`
  display:flex;
  border-bottom:1px solid #eff2f5;
  padding:15px;
`
const BottomContainer = styled.div`
  display:flex;
  justify-content:space-evenly;
`
const Form = styled.form`
  display:flex;
  flex:1;

`
const IputStyle = css`
  outline-width:0px;
  border:none;
  padding:5px 20px;
  margin:0px 10px;
  border-radius:999px;
  background-color:#eff2f5;
`
const Description = styled.input`
  ${IputStyle}
  flex:1;
`
const Prix = styled.input`
  ${IputStyle}
`
const Protable = styled.input`
  ${IputStyle}
`
const BottomOption = styled.div`
  padding:20px;
  display:flex;
  align-items:center;
  color:gray;
  margin:5px;
  h3{
    font-size:medium;
    margin-left:10px;
  }
  &:hover{
    background-color:#eff2f5;
    border-radius:20px;
    cursor:pointer;
  }
`
export const MessageSender = () => {
  const handleSubmit = (e) => {
    e.preventDefault();
  }
  return (
    <DivContainer>
      <TopContainer>
        <Avatar />
        <Form>
        <Description/>
        <Prix type='number' placeholder='Prix'/>
        <Protable type='number' placeholder='Numero de telephone'/>
        <button onClick={handleSubmit} type='submit' style={{ display: 'none' }}>submit button</button>
        </Form>
        </TopContainer>
      <BottomContainer>
        <BottomOption>
          <VideocamIcon style={{ color: 'red' }} />
          <h3>Live Video</h3>
        </BottomOption>
        <BottomOption>
          <PhotoLibraryIcon style={{ color: 'green' }}/>
          <h3>Photos/Videos</h3>
        </BottomOption>
        </BottomContainer>
    </DivContainer>
  )
}
